import UIKit

class PassedCompetitionRouter: PassedCompetitionWireframeProtocol {

    weak var viewController: UIViewController?

    static func createModule() -> UIViewController {
        // Change to get view from storyboard if not using progammatic UI
        let view = PassedCompetitionViewController(nibName: nil, bundle: nil)
        let interactor = PassedCompetitionInteractor()
        let router = PassedCompetitionRouter()
        let presenter = PassedCompetitionPresenter(interface: view, interactor: interactor, router: router)

        view.presenter = presenter
        interactor.presenter = presenter
        router.viewController = view

        return view
    }
}
