import UIKit

class DailyMissionRouter: DailyMissionWireframeProtocol {

    weak var viewController: UIViewController?

    static func createModule() -> UIViewController {
        // Change to get view from storyboard if not using progammatic UI
        let view = DailyMissionViewController(nibName: nil, bundle: nil)
        let interactor = DailyMissionInteractor()
        let router = DailyMissionRouter()
        let presenter = DailyMissionPresenter(interface: view, interactor: interactor, router: router)

        view.presenter = presenter
        interactor.presenter = presenter
        router.viewController = view

        return view
    }
}
