import UIKit

class DailyMissonRouter: DailyMissonWireframeProtocol {

    weak var viewController: UIViewController?

    static func createModule() -> UIViewController {
        // Change to get view from storyboard if not using progammatic UI
        let view = DailyMissonViewController(nibName: nil, bundle: nil)
        let interactor = DailyMissonInteractor()
        let router = DailyMissonRouter()
        let presenter = DailyMissonPresenter(interface: view, interactor: interactor, router: router)

        view.presenter = presenter
        interactor.presenter = presenter
        router.viewController = view

        return view
    }
    
    func gotoDailyMisson() {
        let vc = NameExerciseRouter.createModule(isTaskDate: true)
        viewController?.push(controller: vc)
    }
}
