import UIKit

class NoteListRouter: NoteListWireframeProtocol {

    weak var viewController: UIViewController?

    static func createModule() -> UIViewController {
        // Change to get view from storyboard if not using progammatic UI
        let view = NoteListViewController(nibName: nil, bundle: nil)
        let interactor = NoteListInteractor()
        let router = NoteListRouter()
        let presenter = NoteListPresenter(interface: view, interactor: interactor, router: router)

        view.presenter = presenter
        interactor.presenter = presenter
        router.viewController = view

        return view
    }
    
    func gotoNote(idNote: String){
        let vc = SeeNoteRouter.createModule(idNote: idNote)
        vc.deleagate = viewController as! NoteListViewController
        self.viewController?.push(controller: vc,animated: true)
    }
    
    func gotoAddNote(){
        let vc = AddNoteRouter.createModule()
        vc.deleagate = viewController as! NoteListViewController
        self.viewController?.push(controller: vc,animated: true)
    }
    
    func gotoDetailVocabulary(){
        let vc = DetailLessonRouter.createModule()
        self.viewController?.push(controller: vc,animated: true)
    }
    
    func gotoDetailGrammar(){
        let vc = DetailLessonRouter.createModule()
        self.viewController?.push(controller: vc,animated: true)
    }
}
