import UIKit

class VocabularyRouter: VocabularyWireframeProtocol {

    weak var viewController: UIViewController?

    static func createModule() -> UIViewController {
        // Change to get view from storyboard if not using progammatic UI
        let view = VocabularyViewController(nibName: nil, bundle: nil)
        let interactor = VocabularyInteractor()
        let router = VocabularyRouter()
        let presenter = VocabularyPresenter(interface: view, interactor: interactor, router: router)

        view.presenter = presenter
        interactor.presenter = presenter
        router.viewController = view

        return view
    }
}
