import UIKit

class NameExerciseRouter: NameExerciseWireframeProtocol {

    weak var viewController: UIViewController?

    static func createModule() -> UIViewController {
        // Change to get view from storyboard if not using progammatic UI
        let view = NameExerciseViewController(nibName: nil, bundle: nil)
        let interactor = NameExerciseInteractor()
        let router = NameExerciseRouter()
        let presenter = NameExercisePresenter(interface: view, interactor: interactor, router: router)

        view.presenter = presenter
        interactor.presenter = presenter
        router.viewController = view

        return view
    }
    
    func gotoDetailVocabulary(){
        let vc = DetailLessonRouter.createModule(titleNavi: LocalizableKey.vocabulary.showLanguage,type: .vocabulary)
        self.viewController?.push(controller: vc,animated: true)
    }
    
    func gotoResult() {
        let vc = ResultRouter.createModule(type: .resultExercise)
        self.viewController?.push(controller: vc,animated: true)
    }
}
