import UIKit

class HistoryCompetitionPresenter: HistoryCompetitionPresenterProtocol, HistoryCompetitionInteractorOutputProtocol {

    weak private var view: HistoryCompetitionViewProtocol?
    var interactor: HistoryCompetitionInteractorInputProtocol?
    private let router: HistoryCompetitionWireframeProtocol

    init(interface: HistoryCompetitionViewProtocol, interactor: HistoryCompetitionInteractorInputProtocol?, router: HistoryCompetitionWireframeProtocol) {
        self.view = interface
        self.interactor = interactor
        self.router = router
    }
    
    func getListResultFight(offset: Int, date: String) {
        Provider.shared.competitionAPIService.getListResultFight(offset: offset, date: date, success: { (respone) in
            self.view?.reloadView(data: respone?.results ?? [])
        }) { (_) in
        }
    }

}
