import UIKit

class HistoryCompetitionRouter: HistoryCompetitionWireframeProtocol {

    weak var viewController: UIViewController?

    static func createModule() -> UIViewController {
        // Change to get view from storyboard if not using progammatic UI
        let view = HistoryCompetitionViewController(nibName: nil, bundle: nil)
        let interactor = HistoryCompetitionInteractor()
        let router = HistoryCompetitionRouter()
        let presenter = HistoryCompetitionPresenter(interface: view, interactor: interactor, router: router)

        view.presenter = presenter
        interactor.presenter = presenter
        router.viewController = view

        return view
    }
    
    static func createModule(date: String = "") -> UIViewController {
        // Change to get view from storyboard if not using progammatic UI
        let view = HistoryCompetitionViewController(nibName: nil, bundle: nil)
        let interactor = HistoryCompetitionInteractor()
        let router = HistoryCompetitionRouter()
        let presenter = HistoryCompetitionPresenter(interface: view, interactor: interactor, router: router)
        view.date = date
        
        view.presenter = presenter
        interactor.presenter = presenter
        router.viewController = view
        
        return view
    }
}
