import UIKit

class HistoryListExerciseRouter: HistoryListExerciseWireframeProtocol {

    weak var viewController: UIViewController?

    static func createModule(date: String,type_test: TypeDoExercise = TypeDoExercise.levelExercise) -> UIViewController {
        // Change to get view from storyboard if not using progammatic UI
        let view = HistoryListExerciseViewController(nibName: nil, bundle: nil)
        view.type = type_test
        view.date = date
        let interactor = HistoryListExerciseInteractor()
        let router = HistoryListExerciseRouter()
        let presenter = HistoryListExercisePresenter(interface: view, interactor: interactor, router: router)

        view.presenter = presenter
        interactor.presenter = presenter
        router.viewController = view

        return view
    }
}
