import UIKit

class LessonRouter: LessonWireframeProtocol {

    weak var viewController: UIViewController?

    static func createModule() -> UIViewController {
        // Change to get view from storyboard if not using progammatic UI
        let view = LessonViewController(nibName: nil, bundle: nil)
        let interactor = LessonInteractor()
        let router = LessonRouter()
        let presenter = LessonPresenter(interface: view, interactor: interactor, router: router)

        view.presenter = presenter
        interactor.presenter = presenter
        router.viewController = view

        return view
    }
    static func createModule(type: TheoryType = .lesson) -> LessonViewController {
        // Change to get view from storyboard if not using progammatic UI
        let view = LessonViewController(nibName: nil, bundle: nil)
        view.type = type
        let interactor = LessonInteractor()
        let router = LessonRouter()
        let presenter = LessonPresenter(interface: view, interactor: interactor, router: router)
        
        view.presenter = presenter
        interactor.presenter = presenter
        router.viewController = view
        
        return view
    }
    
    func gotoLesson(){
        let vc = ListLessonRouter.createModule(type: .lesson)
        self.viewController?.push(controller: vc, animated: true)
    }
    
    func gotoRecipe() {
        let vc = ListLessonRouter.createModule(type: .recipe)
        self.viewController?.push(controller: vc, animated: true)
    }
}
