import UIKit

class TheoryRouter: TheoryWireframeProtocol {

    weak var viewController: UIViewController?

    static func createModule() -> UIViewController {
        // Change to get view from storyboard if not using progammatic UI
        let view = TheoryViewController(nibName: nil, bundle: nil)
        let interactor = TheoryInteractor()
        let router = TheoryRouter()
        let presenter = TheoryPresenter(interface: view, interactor: interactor, router: router)

        view.presenter = presenter
        interactor.presenter = presenter
        router.viewController = view

        return view
    }
    
    func gotoSearchTheory(){
        let vc = FindRouter.createModule(type: .searchTheory)
        viewController?.push(controller: vc,animated: true)
    }
}
