import UIKit

class EditProfilePresenter: EditProfilePresenterProtocol, EditProfileInteractorOutputProtocol {

    weak private var view: EditProfileViewProtocol?
    var interactor: EditProfileInteractorInputProtocol?
    private let router: EditProfileWireframeProtocol

    init(interface: EditProfileViewProtocol, interactor: EditProfileInteractorInputProtocol?, router: EditProfileWireframeProtocol) {
        self.view = interface
        self.interactor = interactor
        self.router = router
    }

    func updateProfile(userInfo: UpdateProfileParam) {
        interactor?.updateProfile(userInfo: userInfo)
    }
    
    func updateAvatar(image: UIImage) {
        interactor?.updateAvatar(image: image)
    }
    
    func didUpdateAvatar() {
        view?.didUpdateAvatar()
    }
    
    func didSuccessUpdateProfile(user: UserEntity?) {
        view?.didSuccessUpdateProfile(user: user)
    }
    
    func didErrorUpdateProfile(error: APIError?) {
        view?.didErrorUpdateProfile(error: error)
    }

}
