        super.viewDidLoad()
        
        listHistory = HistoryEntity.toArray()
    }
    
    override func setUpViews() {
        super.setUpViews()
        
        configureTable()
    }
    
    override func setUpNavigation() {
        super.setUpNavigation()
        addBackToNavigation()
