}


extension PreviewProfileViewController: UITableViewDelegate, UITableViewDataSource {
    func configureTable() {
        tbHistory.delegate = self
        tbHistory.dataSource = self
        tbHistory.registerXibFile(HistoryCell.self)
        tbHistory.separatorStyle = .none
        
        tbHistory.estimatedRowHeight = 55
        tbHistory.rowHeight = UITableView.automaticDimension
    }
    
    func tableView(_ tableView: UITableView, cellForRowAt indexPath: IndexPath) -> UITableViewCell {
        let cell = tableView.dequeue(HistoryCell.self, for: indexPath)
       cell.history = listHistory[indexPath.item]
        return cell
    }
    
    func tableView(_ tableView: UITableView, numberOfRowsInSection section: Int) -> Int {
        return listHistory.count
    }
    
